import React, { useState } from 'react';
import './Card.css';
import { supabase } from '../client';

const Card = (props) => {
    const [count, setCount] = useState(props.betCount);

    const updateCount = async (event) => {
        event.preventDefault();

        try {
            await supabase
                .from('Posts')
                .update({ betCount: count + 1 })
                .eq('id', props.id);

            setCount((prevCount) => prevCount + 1);
            console.log("Bet count updated successfully!");
        } catch (error) {
            console.error("Error updating bet count:", error.message);
        }
    };

    return (
        <div className="card">
            <h2>{props.title}</h2>
            <p>Author: {props.author}</p>
            <p>Description: {props.description}</p>
            <p>Bet Count: {count}</p>
            <button onClick={updateCount}>Bet</button>
        </div>
    );
};

export default Card;
